package operator;

public class bitwiseOperator {

//	bitwise operator &, ^, | 
	
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int a = 51, b= 26;
		
		System.out.println((a>b) & (a<b));
		System.out.println((a>b) | (a<b));
		System.out.println((a>b) ^ (a<b));
	}

}
